#
# TABLE STRUCTURE FOR: a_agendamentos
#

DROP TABLE IF EXISTS `a_agendamentos`;

CREATE TABLE `a_agendamentos` (
  `id_agenda` int(11) NOT NULL AUTO_INCREMENT,
  `titulo` varchar(150) NOT NULL,
  `id_espaco` int(11) NOT NULL,
  `data` date NOT NULL,
  `horas` varchar(50) NOT NULL,
  `horas_fim` varchar(50) NOT NULL,
  `id_recurso` int(11) NOT NULL,
  `q_recursos` int(11) NOT NULL,
  `obs` text NOT NULL,
  PRIMARY KEY (`id_agenda`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: a_espacos
#

DROP TABLE IF EXISTS `a_espacos`;

CREATE TABLE `a_espacos` (
  `id_espaco` int(11) NOT NULL AUTO_INCREMENT,
  `nome_espaco` varchar(150) NOT NULL,
  PRIMARY KEY (`id_espaco`)
) ENGINE=MyISAM AUTO_INCREMENT=31 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: a_recursos
#

DROP TABLE IF EXISTS `a_recursos`;

CREATE TABLE `a_recursos` (
  `id_recurso` int(11) NOT NULL AUTO_INCREMENT,
  `nome_recurso` varchar(100) NOT NULL,
  `qtd_recursos` int(11) NOT NULL,
  PRIMARY KEY (`id_recurso`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: d_aps
#

DROP TABLE IF EXISTS `d_aps`;

CREATE TABLE `d_aps` (
  `id_ap` int(11) NOT NULL AUTO_INCREMENT,
  `ip_ap` varchar(15) NOT NULL,
  `local_ap` text NOT NULL,
  `ponto_ap` varchar(15) NOT NULL,
  `status_ap` int(11) NOT NULL,
  `ip_novo_ap` varchar(30) NOT NULL,
  `mac_ap` varchar(30) NOT NULL,
  `switch` varchar(30) NOT NULL,
  `porta` int(11) NOT NULL,
  `switch_local` varchar(20) NOT NULL,
  PRIMARY KEY (`id_ap`),
  UNIQUE KEY `ip_ap` (`ip_ap`)
) ENGINE=MyISAM AUTO_INCREMENT=28 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: d_chamados
#

DROP TABLE IF EXISTS `d_chamados`;

CREATE TABLE `d_chamados` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `obs` text NOT NULL,
  `data` date NOT NULL,
  `horas` time NOT NULL,
  `id_sala` int(11) NOT NULL,
  `id_usuario` int(11) NOT NULL,
  `status` int(11) NOT NULL,
  `datares` date NOT NULL,
  `horasres` time NOT NULL,
  `id_usuariores` int(11) NOT NULL,
  `solucao` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=318 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: d_impressoras
#

DROP TABLE IF EXISTS `d_impressoras`;

CREATE TABLE `d_impressoras` (
  `id_impressora` int(11) NOT NULL AUTO_INCREMENT,
  `setor` varchar(30) NOT NULL,
  `modelo_impressora` varchar(50) NOT NULL,
  `numero_serie` varchar(30) NOT NULL,
  `ip_impressora` varchar(15) NOT NULL,
  `nome_impressora` varchar(150) NOT NULL,
  PRIMARY KEY (`id_impressora`)
) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: d_pontos
#

DROP TABLE IF EXISTS `d_pontos`;

CREATE TABLE `d_pontos` (
  `id_ponto` int(11) NOT NULL AUTO_INCREMENT,
  `data` date NOT NULL,
  `entrada` varchar(50) NOT NULL,
  `saida` varchar(50) NOT NULL,
  `diferenca_horas` varchar(150) NOT NULL,
  `obs` text NOT NULL,
  `id_usuario` int(11) NOT NULL,
  PRIMARY KEY (`id_ponto`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: d_salas
#

DROP TABLE IF EXISTS `d_salas`;

CREATE TABLE `d_salas` (
  `id_sala` int(11) NOT NULL AUTO_INCREMENT,
  `sala` varchar(250) NOT NULL,
  `nome_maquina` varchar(250) NOT NULL,
  `ip` varchar(250) NOT NULL,
  `processador` varchar(250) NOT NULL,
  `memoria` int(11) NOT NULL,
  `memoria_tipo` varchar(100) NOT NULL,
  `sistema` int(11) NOT NULL,
  `hd` float NOT NULL,
  `modelo_hd` varchar(150) NOT NULL,
  `ponto_rede` varchar(15) NOT NULL,
  `switch_ip` varchar(15) NOT NULL,
  `switch_porta` varchar(15) NOT NULL,
  `data` date NOT NULL,
  `id_tipo` int(11) NOT NULL,
  `sistema_operacional` varchar(50) NOT NULL,
  `socket` varchar(150) NOT NULL,
  `perifericos` text NOT NULL,
  `setor` varchar(100) NOT NULL,
  `usuario` varchar(100) NOT NULL,
  `patrimonio` varchar(100) NOT NULL,
  `placa_mae` varchar(100) NOT NULL,
  `monitor` varchar(100) NOT NULL,
  `dominio` int(11) NOT NULL,
  `modelo_projetor` varchar(150) NOT NULL,
  `patrimonio_projetor` varchar(30) NOT NULL,
  `horas_projetor` int(11) DEFAULT NULL,
  PRIMARY KEY (`id_sala`)
) ENGINE=InnoDB AUTO_INCREMENT=101 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: d_usuarios
#

DROP TABLE IF EXISTS `d_usuarios`;

CREATE TABLE `d_usuarios` (
  `id_usuario` int(11) NOT NULL AUTO_INCREMENT,
  `usuario` varchar(50) NOT NULL,
  `password` varchar(50) NOT NULL,
  `admin` int(11) NOT NULL,
  `nome` varchar(255) NOT NULL,
  `setor` varchar(20) NOT NULL,
  `jornada_diaria` varchar(8) NOT NULL,
  `entrada` varchar(15) NOT NULL,
  `saida` varchar(15) NOT NULL,
  PRIMARY KEY (`id_usuario`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: d_utilidades
#

DROP TABLE IF EXISTS `d_utilidades`;

CREATE TABLE `d_utilidades` (
  `id_util` int(11) NOT NULL AUTO_INCREMENT,
  `desc_util` text NOT NULL,
  PRIMARY KEY (`id_util`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=latin1;



